const Comments=(props)=>{
    console.log(props.showComment)

    const showComments = props.showComment.map((comm)=><li>{comm}</li>)
    return(
        <>
        {props.showComment.length < 1 && <h4>There are no comments yet.</h4>}
        {props.showComment.length > 0 && <h5>{showComments}</h5>}
        </>
    )
}

export default Comments
