import { useParams } from "react-router-dom";
import { useContext,useCallback,useEffect,useState } from "react";
import AuthContext from "../store/auth-context";
import { Link } from "react-router-dom";
import useHttp from "../store/use-http";
import { getAllItems } from "../store/api";
import CommentForm from "../components/StartingPage/CommentForm";
import Comments from "../components/StartingPage/Comments";

const VideoPage=()=>{
    const params = useParams()
    const { idVideo,idCategory } = params;
    const [videos, setVideos] = useState([]);
    const [comments,setComments]=useState([]);
    const [isFetched, setIsFetched] = useState(false);

    const {
        sendRequest,
        status,
        data: data,
        error,
      } = useHttp(getAllItems, true);
    
      useEffect(() => {
        sendRequest();
        setVideos(data);
        setIsFetched(true)
      }, [sendRequest]);


        let renderen=[]

        if(status==='completed'){
        let catVideos=[];
        const propertysValues = Object.values(data);
       catVideos.push(...propertysValues)
        let relevantVideo= catVideos.filter((f)=>(f.category)===idCategory && (f.link)!==idVideo);
        renderen = relevantVideo.map((m)=><li key={Math.random()}><Link to={`/videos/${m.category}/${m.link}`}>{m.link}</Link></li>)
}

if (error) {
    return <div>{error}</div>;
  }

  let updatedComms=[]
  const commentsHnadler=(v)=>{
    setComments(prevComms=>{
        updatedComms = [...prevComms];
        updatedComms.push(v)
        return updatedComms;
    })
  }

    return(
        <>
        <h1>{idVideo}</h1>
        <h2>Relative videos for {idCategory} category</h2>
        <h4>{renderen}</h4>
        <CommentForm onComments={commentsHnadler}/>
        <Comments showComment={comments}/>
        </>
    )
}

export default VideoPage;
