import { useContext,useEffect, useState,useCallback } from "react";
import { getAllItems } from "../../store/api";
import AuthContext from '../../store/auth-context';
import classes from './StartingPageContent.module.css';

const StartingPageContent = () => {
  const [videos, setVideos] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const authCtx = useContext(AuthContext);
  const isLoggedIn = authCtx.isLoggedIn;


  const fetchMoviesHandler = useCallback(async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch("https://blindside-test-default-rtdb.firebaseio.com/videos.json");
      if (!response.ok) {
        throw new Error("sth wrong");
      }
      const data = await response.json();

      // const tranformedMovies = data.results.map((movieData) => {
      //   return {
      //     id: movieData.episode_id,
      //     title: movieData.title,
      //     openingText: movieData.opening_crawl,
      //     releaseDate: movieData.release_date,
      //   };
      // });
      setVideos(data);
    } catch (error) {
      setError(error.message);
    }
    setIsLoading(false);
  }, []);

  useEffect(()=>{
    fetchMoviesHandler()},[fetchMoviesHandler])


  return (
    <section className={classes.starting}>
      {isLoggedIn && <h1>Welcome on Board!</h1>}
      {!isLoggedIn && <h1>Please login to see the content.</h1>}
    </section>
  );
};

export default StartingPageContent;
