import { useParams } from "react-router-dom";
import { useContext,useCallback,useEffect,useState } from "react";
import AuthContext from "../store/auth-context";
import { Link } from "react-router-dom";
import useHttp from "../store/use-http";
import { getAllItems } from "../store/api";
import CommentForm from "../components/StartingPage/CommentForm";
import Comments from "../components/StartingPage/Comments";

const VideoPage=()=>{
    const params = useParams()
    const { idVideo,idCategory } = params;
    const [videos, setVideos] = useState([]);
    const [comments,setComments]=useState([]);
    const [isFetched, setIsFetched] = useState(false);

    const {
        sendRequest,
        status,
        data: data,
        error,
      } = useHttp(getAllItems, true);
      
      useEffect(() => {
        setComments([])
          sendRequest();
          setVideos(data);
          setIsFetched(true)
        }, [sendRequest,params]);
        
        
        let renderen=[]
        let showVideo =[]

        if(status==='completed'){
        let catVideos=[];
        const propertysValues = Object.values(data);
        catVideos.push(...propertysValues)
        showVideo=catVideos.filter((f)=>(f.category)===idCategory && (f.title)===idVideo);
        // console.log(showVideo[0].link)
        let relevantVideo= catVideos.filter((f)=>(f.category)===idCategory && (f.title)!==idVideo);
        renderen = relevantVideo.map((m)=><li key={Math.random()}><Link to={`/videos/${m.category}/${m.link}`}>{m.link}</Link></li>)
}

if (error) {
    return <div>{error}</div>;
  }

  let updatedComms=[]
  const commentsHnadler=(v)=>{
    setComments(prevComms=>{
        updatedComms = [...prevComms];
        updatedComms.push(v)
        return updatedComms;
    })
  }

    return(
        <>
        {status==='completed' ? <> <video width="320" height="240" controls>
  <source src={showVideo[0].link} type="video/mp4"/>
  <source src={showVideo[0].link} type="video/ogg"/>
Your browser does not support the video tag.
</video>
<h1>{showVideo[0].link}</h1>
        <h2>Relative videos for {idCategory} category</h2>
        <h4>{renderen}</h4>
        <CommentForm onComments={commentsHnadler}/>
        <Comments showComment={comments}/> </> : <h1>LOADING...</h1>}
        
        </>
    )
}

export default VideoPage;
