const FIREBASE_DOMAIN = "https://blindside-test-default-rtdb.firebaseio.com";

export async function getAllItems() {
  const response = await fetch(`${FIREBASE_DOMAIN}/videos.json`);
  const data = await response.json();

  if (!response.ok) {
    throw new Error(data.message || "Could not fetch videos.");
  }

  const fetchedItems = [];
  for (const key in data) {
    const itemObj = {
      id: key,
      ...data[key],
    };
    fetchedItems.push(itemObj);
  }
  return fetchedItems;
}
