import { useState } from "react"

const Comments=(props)=>{
    console.log(props.showComment)
    const [showComms,setShowComms] = useState(false);

    const showHandler=(e)=>{
        e.preventDefault()
        setShowComms(prevStatus=>!prevStatus)
    }
    console.log(showComms)

    let content = props.showComment.length < 1 ? <h3>There are no comments yet.</h3> :
    props.showComment.length > 0 && <h4>{showComments}</h4>

    const showComments = props.showComment.map((comm)=><li key={Math.random()}>{comm}</li>)
    return(
        <>
        <button onClick={showHandler}>{!showComms ? 'Show comments' : 'Hide comments'}</button>
        {!showComms && content}
        
        </>
    )
}

export default Comments
