import { useParams } from "react-router-dom";
import { useContext,useCallback,useEffect } from "react";
import AuthContext from "../store/auth-context";
import { Link } from "react-router-dom";

const VideoPage=()=>{
    const params = useParams()
    const { idVideo,idCategory } = params;

    const [videos, setVideos] = useState([]);
    const [isFetched, setIsFetched] = useState(false);
    const [error, setError] = useState(null);

    const fetchMoviesHandler = useCallback(async () => {
        setError(null);
        try {
          const response = await fetch("https://blindside-test-default-rtdb.firebaseio.com/videos.json");
          if (!response.ok) {
            throw new Error("sth wrong");
          }
          const data = await response.json();
          setVideos(data);
          authCtx.videosList(data)
        } catch (error) {
          setError(error.message);
        }
        setIsFetched(true);
      }, []);
    
      useEffect(()=>{
        fetchMoviesHandler()},[fetchMoviesHandler])

    const authCtx=useContext(AuthContext)
    let catVideos=[];
    const propertysValues = Object.values(authCtx.videos);
       catVideos.push(...propertysValues)
    let relevantVideo= catVideos.filter((f)=>(f.category)===idCategory && (f.link)!==idVideo);
    let renderen = relevantVideo.map((m)=><li key={Math.random()}><Link to={`/videos/${m.category}/${m.link}`}>{m.link}</Link></li>)
    return(
        <>
        <h1>{idVideo}</h1>
        <h2>Relative videos for {idCategory} category</h2>
        <h4>{renderen}</h4>
        </>
    )
}

export default VideoPage;
